import React from 'react';
import plantImg from '../assets/plant.png';

const HomeView = () => {
    return (
        <div className="flex flex-col justify-between items-center h-screen bg-green-50 relative overflow-hidden">

            {/* Main Content */}
            <div className="flex flex-col items-center justify-center text-center px-9 py-20">
                {/* Plant image */}
                <img
                    src={plantImg}
                    alt="Fresh Plant"
                    className="w-40 h-40 mb-4"
                />

                {/* Title */}
                <h1 className="text-2xl font-bold text-green-600 mb-8">Bienvenidos a Bachue AI</h1>

                {/* Description */}
                <p className="text-gray-500 text-md mb-6">
                    El objetivo del proyecto Bachué es desarrollar un
                    sistema de detección automática de
                    enfermedades en hojas de papa a partir de
                    imágenes tomadas con dispositivos móviles,
                    utilizando modelos de visión por computador
                    como YOLO, con el fin de brindar diagnósticos
                    rápidos y accesibles a pequeños agricultores.
                </p>

            </div>

            {/* Decorative Waves at Bottom */}
            {/* Decorative Waves at Bottom */}
<div className="absolute bottom-0 w-full overflow-hidden">
    {/* Capa 1 - fondo más oscuro */}
    <svg viewBox="0 0 1440 320" className="w-full h-48" preserveAspectRatio="none">
        <path
            fill="#15803d"
            d="M0,256L60,229.3C120,203,240,149,360,154.7C480,160,600,224,720,229.3C840,235,960,181,1080,165.3C1200,149,1320,171,1380,181.3L1440,192L1440,320L1380,320C1320,320,1200,320,1080,320C960,320,840,320,720,320C600,320,480,320,360,320C240,320,120,320,60,320L0,320Z"
        />
    </svg>

    {/* Capa 2 - tono medio, solapada */}
    <svg viewBox="0 0 1440 320" className="w-full h-52 -mt-24" preserveAspectRatio="none">
        <path
            fill="#16a34a"
            fillOpacity="0.9"
            d="M0,224L60,202.7C120,181,240,139,360,144C480,149,600,203,720,208C840,213,960,171,1080,165.3C1200,160,1320,192,1380,202.7L1440,213L1440,320L1380,320C1320,320,1200,320,1080,320C960,320,840,320,720,320C600,320,480,320,360,320C240,320,120,320,60,320L0,320Z"
        />
    </svg>

    {/* Capa 3 - más clara y al frente */}
    <svg viewBox="0 0 1440 320" className="w-full h-56 -mt-28" preserveAspectRatio="none">
        <path
            fill="#22c55e"
            d="M0,288L60,272C120,256,240,224,360,208C480,192,600,192,720,202.7C840,213,960,235,1080,240C1200,245,1320,235,1380,229.3L1440,224L1440,320L1380,320C1320,320,1200,320,1080,320C960,320,840,320,720,320C600,320,480,320,360,320C240,320,120,320,60,320L0,320Z"
        />
    </svg>
</div>

        </div>
    );
};

export default HomeView;
