import React from 'react';
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';
import { CSSTransition, TransitionGroup } from 'react-transition-group'; // Importamos para las transiciones
import HomeView from './views/HomeView';
import CropRecommendationView from './views/CropRecommendationView';
import Navbar from './components/Navbar';

const App = () => {
    return (
        <Router>
            {/* El Navbar se mantiene fuera del grupo de transición para que no se anime con el contenido de la página */}
            <Navbar />

            {/*
                Usamos el componente Route con la prop 'render' para acceder al objeto 'location'.
                Esto es crucial para que TransitionGroup y CSSTransition sepan cuándo cambia la ruta.
            */}
            <Route render={({ location }) => (
                <TransitionGroup>
                    {/*
                        CSSTransition envuelve el Switch.
                        - 'key' es importante: debe cambiar con cada ruta para que la transición se active. Usamos location.key.
                        - 'timeout' define la duración de la animación en milisegundos.
                        - 'classNames' es el prefijo para las clases CSS que controlarán la animación (ej. fade-enter, fade-enter-active).
                    */}
                    <CSSTransition
                        key={location.key}
                        timeout={500} // La animación durará 500 milisegundos
                        classNames="fade" // Prefijo para las clases CSS de transición
                    >
                        {/*
                            El Switch necesita la prop 'location' cuando está dentro de TransitionGroup
                            para asegurarse de que renderiza el componente correcto para la ruta actual.
                        */}
                        <Switch location={location}>
                            <Route path="/" exact component={HomeView} />
                            <Route path="/inicio" component={CropRecommendationView} />
                        </Switch>
                    </CSSTransition>
                </TransitionGroup>
            )} />

            {/*
                Estilos CSS para las animaciones de transición.
                Estas clases se aplicarán automáticamente por CSSTransition.
                Hemos añadido un efecto de deslizamiento y desvanecimiento para una transición "linda".
            */}
            <style>
                {`
                /* Estilo base para los componentes de página */
                .fade-enter {
                    opacity: 0;
                    transform: translateX(100%); /* La nueva página comienza fuera de la pantalla a la derecha */
                    position: absolute; /* Asegura que las páginas se superpongan durante la transición */
                    width: 100%; /* Asegura que la página ocupe todo el ancho */
                }
                .fade-enter-active {
                    opacity: 1;
                    transform: translateX(0%); /* La nueva página se desliza hacia el centro */
                    transition: opacity 500ms ease-in-out, transform 500ms ease-in-out; /* Duración y tipo de transición */
                    position: absolute;
                    width: 100%;
                }
                .fade-exit {
                    opacity: 1;
                    transform: translateX(0%); /* La página actual comienza en el centro */
                    position: absolute;
                    width: 100%;
                }
                .fade-exit-active {
                    opacity: 0;
                    transform: translateX(-100%); /* La página actual se desliza fuera de la pantalla a la izquierda */
                    transition: opacity 500ms ease-in-out, transform 500ms ease-in-out;
                    position: absolute;
                    width: 100%;
                }

                /* Contenedor para manejar el posicionamiento absoluto de las páginas */
                .transition-group {
                    position: relative;
                    width: 100%;
                    height: 100%; /* O la altura que necesites para tu contenido */
                    overflow: hidden; /* Oculta el contenido que se desliza fuera de la vista */
                }
                `}
            </style>
        </Router>
    );
};

export default App;
