import React, { useState, useRef } from 'react';

const CropRecommendationView = () => {
  const [isModalOpen, setIsModalOpen] = useState(false);

  const fileInputRef = useRef(null);
  const cameraInputRef = useRef(null);

  const handleOption = (option) => {
    setIsModalOpen(false);
    if (option === 'upload') {
      fileInputRef.current.click();
    } else if (option === 'camera') {
      cameraInputRef.current.click();
    }
  };

  const handleFileChange = (event) => {
    const file = event.target.files[0];
    if (file) {
      // Aquí puedes redirigir, subir o procesar la imagen como desees
      console.log('Imagen seleccionada:', file.name);
      alert(`Imagen cargada: ${file.name}`);
    }
  };

  return (
    <div className="bg-green-50 min-h-screen flex flex-col">
      <div className="container mx-auto p-6 text-center">
        <h1 className="text-2xl font-bold text-green-600 mb-6">INSTRUCCIONES</h1>

        <ol className="text-gray-800 text-md list-decimal list-inside mb-10 space-y-3 text-left max-w-xl mx-auto">
          <li>Sube o toma una foto clara de tu cultivo.</li>
          <li>Asegúrate de que la imagen esté bien iluminada para obtener mejores resultados.</li>
          <li>¡Listo! Recibirás recomendaciones automáticamente.</li>
        </ol>

        <button
          onClick={() => setIsModalOpen(true)}
          className="bg-green-500 text-white w-16 h-16 flex items-center justify-center rounded-full shadow-md hover:bg-green-600 transition mx-auto"
        >
          <svg
            xmlns="http://www.w3.org/2000/svg"
            className="h-7 w-7 text-white"
            fill="none"
            viewBox="0 0 24 24"
            stroke="currentColor"
          >
            <path
              strokeLinecap="round"
              strokeLinejoin="round"
              strokeWidth={2}
              d="M3 7h2l2-3h6l2 3h2a2 2 0 012 2v9a2 2 0 01-2 2H5a2 2 0 01-2-2V9a2 2 0 012-2z"
            />
            <circle cx="12" cy="13" r="4" stroke="currentColor" strokeWidth="2" fill="none" />
          </svg>
        </button>
      </div>

      {/* Modal */}
      {isModalOpen && (
        <div className="fixed inset-0 bg-black bg-opacity-40 flex items-center justify-center z-50">
          <div className="bg-white rounded-2xl shadow-lg w-80 p-6">
            <h2 className="text-xl font-semibold text-green-600 mb-4">¿Qué deseas hacer?</h2>
            <div className="flex flex-col gap-4">
              <button
                onClick={() => handleOption('upload')}
                className="bg-green-500 text-white py-2 px-4 rounded hover:bg-green-600 transition"
              >
                Subir desde galería
              </button>
              <button
                onClick={() => handleOption('camera')}
                className="bg-green-400 text-white py-2 px-4 rounded hover:bg-green-500 transition"
              >
                Tomar una foto
              </button>
              <button
                onClick={() => setIsModalOpen(false)}
                className="text-gray-600 hover:underline text-sm mt-2"
              >
                Cancelar
              </button>
            </div>
          </div>
        </div>
      )}

      {/* Inputs invisibles */}
      <input
        type="file"
        accept="image/*"
        ref={fileInputRef}
        style={{ display: 'none' }}
        onChange={handleFileChange}
      />
      <input
        type="file"
        accept="image/*"
        capture="environment"
        ref={cameraInputRef}
        style={{ display: 'none' }}
        onChange={handleFileChange}
      />
    </div>
  );
};

export default CropRecommendationView;
