import React from 'react';
import plantImg from '../assets/plant.png'; // Asegúrate de que esta ruta sea correcta

const HomeView = () => {
    return (
        // Contenedor principal de la vista.
        // Ocupa toda la altura disponible (ajustada por el padding del App.jsx).
        // Flexbox para centrar el contenido vertical y horizontalmente.
        // Posicionamiento relativo para los elementos decorativos.
        <div className="flex flex-col items-center justify-center min-h-[calc(100vh-7rem)] relative overflow-hidden px-4 sm:px-6 lg:px-8">
            {/*
                Elementos decorativos de fondo (ondas o formas sutiles)
                para realzar el efecto glassmorphism y la temática campestre.
                Puedes ajustar los colores y la opacidad.
            */}
            {/* Onda superior izquierda */}
            <div className="absolute top-0 left-0 w-64 h-64 bg-green-300 rounded-full mix-blend-multiply filter blur-3xl opacity-20 animate-blob"></div>
            {/* Onda inferior derecha */}
            <div className="absolute bottom-0 right-0 w-72 h-72 bg-green-400 rounded-full mix-blend-multiply filter blur-3xl opacity-20 animate-blob animation-delay-2000"></div>
            {/* Onda central (detrás del card) */}
            <div className="absolute top-1/2 left-1/2 -translate-x-1/2 -translate-y-1/2 w-80 h-80 bg-green-200 rounded-full mix-blend-multiply filter blur-3xl opacity-20 animate-blob animation-delay-4000"></div>

            {/*
                Tarjeta principal con efecto Glassmorphism.
                - max-w-4xl: Ancho máximo para que no ocupe toda la pantalla en escritorios grandes.
                - mx-auto: Centra la tarjeta horizontalmente.
                - p-8 sm:p-12: Padding responsivo.
                - rounded-3xl: Esquinas muy redondeadas para un aspecto suave y elegante.
                - shadow-2xl: Sombra pronunciada para dar la sensación de flotar.
                - z-10: Asegura que la tarjeta esté por encima de los elementos decorativos de fondo.
                - font-serif: Aplica la fuente Tinos a todo el contenido de la tarjeta.
            */}
            <div
                className="relative max-w-4xl mx-auto p-8 sm:p-12 rounded-3xl shadow-2xl z-10 text-center font-serif"
                style={{
                    background: 'linear-gradient(135deg, rgba(255, 255, 255, 0.15), rgba(255, 255, 255, 0.05))', // Fondo blanco muy transparente
                    backdropFilter: 'blur(20px) saturate(180%)', // Desenfoque fuerte para el cristal
                    WebkitBackdropFilter: 'blur(20px) saturate(180%)', // Prefijo para compatibilidad
                    border: '1px solid rgba(255, 255, 255, 0.3)', // Borde blanco sutil
                    boxShadow: '0 15px 50px 0 rgba(0, 0, 0, 0.2)' // Sombra más grande y difusa
                }}
            >
                {/* Imagen de la planta */}
                <img
                    src={plantImg}
                    alt="Fresh Plant"
                    className="w-48 h-48 mx-auto mb-6 object-contain drop-shadow-lg" // Centrada, más grande, con sombra
                />

                {/* Título principal */}
                <h1 className="text-4xl sm:text-5xl font-bold text-green-800 mb-6 leading-tight drop-shadow-md">
                    Bienvenidos a <span className="text-green-600">Bachue AI</span>
                </h1>

                {/* Descripción del proyecto */}
                <p className="text-gray-700 text-lg sm:text-xl mb-12 leading-relaxed max-w-2xl mx-auto">
                    El objetivo del proyecto Bachué es desarrollar un sistema de detección automática de
                    enfermedades en hojas de papa a partir de imágenes tomadas con dispositivos móviles,
                    utilizando modelos de visión por computador como YOLO, con el fin de brindar diagnósticos
                    rápidos y accesibles a pequeños agricultores.
                </p>

                {/* Integrantes */}
                <div className="text-gray-600 text-base sm:text-lg leading-relaxed">
                    <strong className="text-green-700">Integrantes:</strong><br />
                    Lizeth Mariana Garcia <br />
                    Nicolas Felipe Arciniegas Lizarazo<br />
                    Juan David Guarnizo<br />
                    Diego Andres Alvarez Gonzalez<br />
                </div>
            </div>

            {/*
                Estilos para la animación de las "ondas" de fondo.
                Estas son animaciones de movimiento sutil y cambio de tamaño
                para dar una sensación orgánica y fluida en el fondo.
            */}
            <style>
                {`
                @keyframes blob {
                    0% { transform: translate(0px, 0px) scale(1); }
                    33% { transform: translate(30px, -50px) scale(1.1); }
                    66% { transform: translate(-20px, 20px) scale(0.9); }
                    100% { transform: translate(0px, 0px) scale(1); }
                }
                .animate-blob {
                    animation: blob 7s infinite cubic-bezier(0.6, 0.01, 0.3, 0.9);
                }
                .animation-delay-2000 {
                    animation-delay: 2s;
                }
                .animation-delay-4000 {
                    animation-delay: 4s;
                }
                `}
            </style>
        </div>
    );
};

export default HomeView;
