import React from 'react';

const HomeView = () => {
    return (
        <div className="flex flex-col items-center justify-center h-screen bg-gradient-to-br from-green-100 via-green-200 to-green-400">
            <h1 className="text-4xl font-extrabold mb-4 text-green-800 drop-shadow-lg">
                Bienvenido a Bachue AI Disease Detection
            </h1>
            <p className="text-lg mb-8 text-green-900">
                Sube una imagen de tus cultivos para obtener predicciones de enfermedades y recomendaciones.
            </p>
            <a
                href="/upload"
                className="px-6 py-3 bg-green-600 text-white rounded-lg shadow hover:bg-green-700 transition-colors font-semibold"
            >
                Comenzar
            </a>
        </div>
    );
};

export default HomeView;
