import React from 'react';
import plantImg from '../assets/plant.png';

const HomeView = () => {
    return (
        <div className="flex flex-col justify-between items-center h-screen bg-green-50 relative overflow-hidden">

            {/* Main Content */}
            <div className="flex flex-col items-center justify-center text-center px-9 py-20">
                {/* Plant image */}
                <img
                    src={plantImg}
                    alt="Fresh Plant"
                    className="w-40 h-40 mb-4"
                />

                {/* Title */}
                <h1 className="text-2xl font-bold text-green-600 mb-8">Bienvenidos a Bachue AI</h1>

                {/* Description */}
                <p className="text-gray-500 text-md mb-6">
                    El objetivo del proyecto Bachué es desarrollar un
                    sistema de detección automática de
                    enfermedades en hojas de papa a partir de
                    imágenes tomadas con dispositivos móviles,
                    utilizando modelos de visión por computador
                    como YOLO, con el fin de brindar diagnósticos
                    rápidos y accesibles a pequeños agricultores.
                </p>

            </div>

            <div className="absolute bottom-0 w-full">
                {/* Wave 1 */}
                <svg viewBox="0 0 1440 320" className="w-full h-72" preserveAspectRatio="none">
                    <path
                        fill="#22c55e"
                        fillOpacity="1"
                        d="M0,224L48,202.7C96,181,192,139,288,133.3C384,128,480,160,576,186.7C672,213,768,235,864,218.7C960,203,1056,149,1152,144C1248,139,1344,181,1392,202.7L1440,224L1440,320L1392,320C1344,320,1248,320,1152,320C1056,320,960,320,864,320C768,320,672,320,576,320C480,320,384,320,288,320C192,320,96,320,48,320L0,320Z"
                    />
                </svg>
                {/* Wave 2 */}
                <svg viewBox="0 0 1440 320" className="w-full h-64 -mt-40" preserveAspectRatio="none">
                    <path
                        fill="#16a34a"
                        fillOpacity="0.7"
                        d="M0,288L48,272C96,256,192,224,288,197.3C384,171,480,149,576,154.7C672,160,768,192,864,197.3C960,203,1056,181,1152,176C1248,171,1344,181,1392,186.7L1440,192L1440,320L1392,320C1344,320,1248,320,1152,320C1056,320,960,320,864,320C768,320,672,320,576,320C480,320,384,320,288,320C192,320,96,320,48,320L0,320Z"
                    />
                </svg>
            </div>
        </div>
    );
};

export default HomeView;
