import React from 'react';
import { Link } from 'react-router-dom';

const Navbar = () => {
    return (
        // Contenedor principal del Navbar con degradado, sombra, esquinas redondeadas y el efecto glassmorphism
        <nav className="relative p-5 shadow-xl rounded-b-3xl overflow-hidden"
             style={{
                 background: 'linear-gradient(to right, rgba(34, 197, 94, 0.3), rgba(22, 163, 74, 0.3))', // Degradado verde con opacidad
                 backdropFilter: 'blur(12px) saturate(180%)', // Efecto de desenfoque y saturación para glassmorphism
                 WebkitBackdropFilter: 'blur(12px) saturate(180%)', // Prefijo para compatibilidad con navegadores
                 borderBottom: '1px solid rgba(255, 255, 255, 0.2)', // Borde sutil en la parte inferior
                 borderRight: '1px solid rgba(255, 255, 255, 0.1)', // Borde sutil en la parte derecha
                 boxShadow: '0 8px 32px 0 rgba(0, 0, 0, 0.15)' // Sombra más pronunciada para el efecto 3D
             }}
        >
            {/* Elementos decorativos para evocar el campo colombiano (opcional, puedes ajustar o quitar) */}
            {/* Un "rayo de sol" o reflejo sutil */}
            <div className="absolute top-0 left-0 w-full h-full"
                 style={{
                     background: 'linear-gradient(135deg, rgba(255,255,255,0.05) 0%, rgba(255,255,255,0) 50%)',
                     pointerEvents: 'none' // Asegura que no interfiera con los clics
                 }}>
            </div>

            {/* Contenedor centralizado para el contenido del Navbar */}
            <div className="container mx-auto flex justify-between items-center relative z-10"> {/* z-10 para asegurar que el contenido esté por encima de los efectos */}
                {/* Nombre de la marca con fuente Comic Neue, más grande y con sombra de texto */}
                <div className="text-white text-4xl font-extrabold font-sans tracking-wide drop-shadow-lg">
                    Bachue AI
                </div>

                {/* Contenedor de los enlaces de navegación */}
                <div>
                    {/* Enlace "Nosotros" con estilos mejorados y efecto de transición */}
                    <Link
                        to="/"
                        className="text-white text-xl font-semibold px-6 py-3 rounded-full hover:bg-white hover:bg-opacity-20 hover:text-white transition duration-300 ease-in-out mx-3 backdrop-blur-sm"
                        style={{ border: '1px solid rgba(255, 255, 255, 0.1)' }} // Borde sutil para los botones
                    >
                        Nosotros
                    </Link>
                    {/* Enlace "Empezar" con estilos mejorados y efecto de transición */}
                    <Link
                        to="/inicio"
                        className="text-white text-xl font-semibold px-6 py-3 rounded-full hover:bg-white hover:bg-opacity-20 hover:text-white transition duration-300 ease-in-out mx-3 backdrop-blur-sm"
                        style={{ border: '1px solid rgba(255, 255, 255, 0.1)' }} // Borde sutil para los botones
                    >
                        Empezar
                    </Link>
                </div>
            </div>
        </nav>
    );
};

export default Navbar;
